def crt2SphPol(cart):
	x = cart[0]
	y = cart[1]
	z = cart[2]
	r = math.sqrt(x*x+y*y+z*z)
	t = math.acos(z/(r))
	p = math.atan2(y,x)
	return(r,t,p)

def sphPol2Crt(sph):
	r = sph[0]
	t = sph[1]
	p = sph[2]
	x = r*math.sin(t)*math.cos(p)
	y = r*math.sin(t)*math.sin(p)
	z = r*math.cos(t)
	return(x,y,z)

def cartDistMag(a,b):
	return np.sum(map(lambda a,b: (a-b)**2,a,b))

def asPolars(sphere):
	polarSphere = []
	for v in sphere:
		polarSphere.append(crt2SphPol(v))
	return polarSphere


def coverSphere(radius,coords):
	newCoords = []
	for c in coords:
		cSph = crt2SphPol(c)
		cSphNew = (radius,cSph[1],cSph[2])
		newCoords.append(sphPol2Crt(cSphNew))
	return newCoords

def getMinimumRadius(coords):
	minD = cartDistMag(coords[0],coords[1])
	for i in range(len(coords)):
		for j in range(i+1,len(coords)):
			c1 = coords[i]
			c2 = coords[j]
			d = cartDistMag(c1,c2)
			if d < minD:
				minD = d
	return minD


def cover72Sphere(radius):
	return coverSphere(radius,icos72)

def cover92Sphere(radius):
	return coverSphere(radius,icos92)

def cover122Sphere(radius):
	return coverSphere(radius,icos122)

def cover132Sphere(radius):
	return coverSphere(radius,icos132)

def cover72SpherePolar(radius):
	return asPolars(cover72Sphere(radius))

def cover92SpherePolar(radius):
	return asPolars(cover92Sphere(radius))

def cover122SpherePolar(radius):
	return asPolars(cover122Sphere(radius))

def cover132SpherePolar(radius):
	return asPolars(cover132Sphere(radius))
